INSERT INTO `FUK_AKTIVNOST` VALUES (3100,3009,3010,'Исплата новца',NULL,9,'','Исплате из благајне имају исплатни назив везан за пословање.Исплатни називи су: рачуни за набављену робу и услуге плаћене готовином,уз претходно одобрење директора или лица које он овласти,исправе о стављању готовине на рачун предузећа,путни налози,друге исправе','Благајник','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:34');
INSERT INTO `FUK_AKTIVNOST` VALUES (3101,3009,3010,'Потврда о исплати',NULL,10,'Потврда о исплати,Потврда о издатој готовини','На основу исплатног назива,за сваку готовину благајну сачињава се потврда о исплати (налог благајни исплатити расход).Благајнички расходи воде се у интерној евиденцији службе,у два примерка,од којих један примерак добија исплатилац,а други прилаже уз налог за књижење.Приликом сваке исплате готовине благајник треба да издаје потврду о издатој готовини - налог благајни исплатити (благајнички расход).Из благајничког расхода треба да се види: датум издате готовине,сврха исплате,име примаоца,износ исплате.','Благајник','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:34');
