INSERT INTO `FUK_AKTIVNOST` VALUES (3104,3009,3011,'Вишак или мањак приликом примопредаје благајне',NULL,2,'','Ако је приликом закључења дневне благајне утврђен вишак или мањак,он се уписује у благајничку евиденцију (благајнички дневник).Утврђени вишак или мањак се приликом примопредаје благајне уписује у записник о примопредаји благајне.Благајник треба да истражи разлике,а разлоге због којих су настали вишкови и мањкови треба да упише у благајнички дневник.','Благајник','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (3105,3009,3011,'Вођење евиденције',NULL,3,'Дневно','Благајнички дневник сачињава се и закључује сваког радног дана готовинског плаћања.Благајнички дневник треба да садржи: датум евидентирања,редни број уплате или исплате (приликом закључка дневника то је податак колико је исправа евидентирано,ове исправе треба приложити уз дневник),редни број уписане благајничке исправе (о уплати или исплати),уплаћени или исплаћени износ,збир дневног промета уплата,податак о стању благајне на почетку дана,податак о висини исплата,податак о утврђеном стању готовине на крају дана,потпис благајника,потпис контисте у рачуноводству приликом пријема благајничког дневника на књижење.Благајник је дужан да на крају радног дана или најкасније наредног радног дана преда благајну на књижење служби рачуноводства.','Благајник','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (3106,3009,3011,'Контрола',NULL,4,'На крају радног дана','Руководилац службе финансија обавља контролу евиденције','Руководилац службе финансија','На крају радног дана',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (3107,3009,3011,'Уписи у благајнички дневник',NULL,5,'','Сви уписи у благајнички дневник треба да се заснивају на благајничким приходима и расходима из којих је за сваки дан посебно могуће утврдити основ за књижење.Приликом уписивања у благајнички дневник,благајник треба да узме у обзир: уписи треба да буду обављени тако да брисање није могуће,код евентуалних исправки треба да се види првобитни упис.','Благајник','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (3108,3009,3011,'Прослеђивање благајничког дневника',NULL,6,'','Благајник сваког дана доставља благајничке дневнике Одељењу књиговодства,ради књижења свих уплата и исплата.','Благајник','Благајник',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (3109,3009,3011,'Закључивање благајничког дневника',NULL,8,'','Благајнички дневник се закључује,потпис Благајника.','Благајник','Благајник',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (3110,3009,3011,'Годишњи извештај',NULL,10,'','Попис благајне врши се једном годишње за потребе састављања годишњег извештаја (завршног рачуна).Када благајник закључи благајнички дневник за последњи дан обрачунског периода,пописна комисија пописује готовину у благајни.Пописна комисија врши попис на крају последњег дана пословне године,односно првог радног дана наредне године.','Благајник','Благајник',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (3111,3009,3011,'Чување документације',NULL,11,'','Благајнички документи (приходи,расходи са прилозима и дневник) чувају се у регистратору,сложени по времену настанка (хронолошки).Благајнички документи који су сачињени у интерној евиденцији Одељења рачуноводства чувају се на носиоцима података у облику који омогућује поновну презентацију у првобитном,непромењеном облику.Благајнички документи чувају се у архиви Предузећа у складу са Законом.','Благајник','Благајник',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:35');
