INSERT INTO `FUK_AKTIVNOST` VALUES (3128,3010,3012,'Надзор нареализованим радовима',NULL,16,'Грађевински дневникГрађевинска књигаМесечна фин.ситуација','Руководилац РЈ грађевинску књигу и грађевински дневник потписује у име извршиоца.Грађевинску књигу и грађевински дневник оверава надзорни орган именован од стране скупштине града-Дирекција за изградњу града.На основу потписаних ситуација од стране надзорног органа попуњава се  формулар месечна финансијска ситуација.','Руководилац РЈНадзорни органАдминистратор РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:37');
