INSERT INTO `FUK_AKTIVNOST` VALUES (3130,3010,3012,'Евиденцијаприговора',NULL,18,'Грађевински дневник','Рекламацију на рад службе евидентира надзорни орган кроз грађевински дневник о евидентираним рекламацијама Администратор РЈ обавештава службу контакт центар која има задатак да се бави обрадом свих приговора.','Администратор РЈНадзорни органКоординатор контакт центар','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:37');
INSERT INTO `FUK_AKTIVNOST` VALUES (3131,3010,3012,'Извештавање',NULL,19,'Обрасци реализације послова,Дневни извештај о раду,Извештај о раду','Руководилац РЈ попуњава дневни извештај о раду који се односи на радну снагу и формулар који се односи на извршене послове и стање пратеће опреме (возила),у који уноси евидентиране рекламације од стране надзорног органа.Дневни извештаји о раду и учинку се доставља Директору сектора одржавања хигијене.','Директор сектора одржавања хигијенеРуководилац РЈ Администратор РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:37');
