INSERT INTO `FUK_AKTIVNOST` VALUES (3167,3013,3016,'Пријем Жалбе',NULL,2,'Закон о општем управном поступку члан 222, члан 223','Жалба се непосредно предаје или шаље поштом органу који је донео првостепено решење.У жалби се мора навести решење које се побија и означити назив органа који га је донео, као и број и датум решења.Довољно је да жалилац у жалби изложи у ком је погледу незадовољан решењем, али жалбу не мора посебно образложити.','Администратор за правне послове','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (3168,3013,3016,'Жалба допуштена',NULL,3,'Закон о општем управном поступку члан 224','Уколико странка уложи жалбу, Првостепени орган испитује да ли је жалба допуштена, благовремена и изјављена од овлашћеног лица.','Руководилац службе правних послова','Без одгоде',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (3169,3013,3016,'Одбацивање жалбе',NULL,4,'Закон о општем управном поступку Члан 224Закључак','Недопуштену, неблаговремену или од неовлашћеног лица изјављену жалбу првостепени орган одбациће својим закључком.','Руководилац службе правних послова','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (3170,3013,3016,'Жалба на закључак',NULL,5,'','Против закључка којим је жалба одбачена на основу става 2.овог члана странка има право на посебну жалбу.','Незадовољна странка','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (3171,3013,3016,'Обустава поступка',NULL,6,'Закључак','Ако странка одустане од жалбе, поступак по жалби обуставља се закључком, против кога није допуштена жалба.Против тог закључка може се непосредно покренути управни спор пред надлежним судом.','Руководилац службе правних послова','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (3172,3013,3016,'Одлука о жалби',NULL,7,'','Ако орган који одлучује по жалби нађе да је жалба оправдана, одлучиће уједно и по жалби која је била одбачена','Руководилац службе правних послова','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (3173,3013,3016,'Промена решења',NULL,8,'','Ако орган који је донео првостепено решење нађе да је жалба основана, а није потребно спроводити нов посебни испитни поступак, може ствар решити друкчије и новим решењем заменити решење које се жалбом побија.','Руководилац службе правних послова','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (3174,3013,3016,'Достава списа другостепеног тела',NULL,9,'','Кад орган који је донео првостепено решење нађе да је поднета жалба допуштена, благовремена и изјављена од овлашћеног лица, а није новим решењем заменио решење које се жалбом побија, дужан је, без одлагања, а најдоцније у року од 15 дана од дана пријема жалбе, доставити жалбу органу надлежном за решавање по жалби.','Руководилац службе правних послова','Без одгоде',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (3175,3013,3016,'Одбијање жалбе',NULL,11,'','Другостепени орган ће одбити жалбу кад утврди да је поступак који је решењу претходио правилно спроведен и да је решење правилно и на закону засновано, а жалба неоснована.Другостепени орган ће одбити жалбу и кад нађе да је у првостепеном поступку било недостатака, али да они нису могли имати утицаја на решење управне ствари.Кад другостепени орган нађе да је првостепено решење засновано на закону, али због других разлога а не због оних који су у решењу наведени, он ће у свом решењу изложити те разлоге, а жалбу одбити.','Другостепени орган','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (3176,3013,3016,'Измена решења',NULL,12,'','Другостепени орган може поводом жалбе изменити првостепено решење у корист жалиоца и мимо захтева постављеног у жалби, а у оквиру захтева постављеног у првостепеном поступку ако се тим не вређа право трећих лица','Другостепени орган','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (3177,3013,3016,'Поништење решења',NULL,13,'','Ако другостепени орган утврди да су у првостепеном решењу погрешно оцењени докази, да је из утврђених чињеница изведен погрешан закључак у погледу чињеничног стања, да је погрешно примењен правни пропис на основу кога се решава управна ствар, или ако нађе да је на основу слободне оцене требало донети друкчије решење, он ће својим решењем поништити првостепено решење и сам решити управну ствар.','Другостепени орган','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:42');
INSERT INTO `FUK_AKTIVNOST` VALUES (3178,3013,3016,'Пријем другостепеног решења и достава странци',NULL,14,'Спис','Орган који је донео другостепено решење шаље, по правилу, своје решење, са списима предмета, првостепеном органу, који је дужан да га достави странкама у року од осам дана од дана пријема списа.','Руководилац службе правних послова','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:42');
INSERT INTO `FUK_AKTIVNOST` VALUES (3179,3013,3016,'Понављање поступка',NULL,15,'Решење','Ако другостепени орган нађе да ће недостатке првостепеног поступка брже и економичније отклонити првостепени орган, он ће својим решењем поништити првостепено решење и вратити предмет првостепеном органу на поновни поступак.У том случају, другостепени орган је дужан својим решењем да укаже првостепеном органу у ком погледу треба допунити поступак, а првостепени орган је дужан у свему да поступи по другостепеном решењу и да, без одлагања, а најдоцније у року од 30 дана од дана пријема предмета, донесе ново решење.','Руководилац службе правних послова','30 дана од дана пријема предмета',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:42');
INSERT INTO `FUK_AKTIVNOST` VALUES (3180,3013,3016,'Странка задовољна',NULL,16,'','Другостепеним решењем или решењем новим поступком странка не мора бити задовољна.Против новог решења странка има право на жалбу.','Незадовољна странка','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:42');
INSERT INTO `FUK_AKTIVNOST` VALUES (3181,3013,3016,'Подизање тужбе пред уставним судом',NULL,17,'Тужба','Незадовољна странка има могућност подизања тужбе пред Уставним судом РС.','Незадовољна странка','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:42');
INSERT INTO `FUK_AKTIVNOST` VALUES (3182,3013,3016,'Достава списа',NULL,18,'Спис','Првостепени орган доставља спис другостепеном телу због покретања управног спора.','Руководилац службе правних послова','Без одгоде',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:42');
