INSERT INTO `FUK_AKTIVNOST` VALUES (3185,3013,3017,'Пријем тужбе или предлог за подизање управног спора',NULL,2,'Тужбе,Предлози','Уколико је потребно водити управни спор примљене тужбе или предлоге за покретање управног спора сектори достављају Руководиоцу службе правних послова','Руководилац службе правних послова','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:42');
INSERT INTO `FUK_AKTIVNOST` VALUES (3186,3013,3017,'Вођење управног спора',NULL,3,'','По примљеним тужбама у којима је ЈКП“Медиана“-Ниш оптужени или примљеним предлозима за покретање управног спора тужбом у којима је ЈКП Медиана“-Ниш тужилац или заинтересована особа у управном спору, Руководилац службе зправних пословаправне послове  управни спор води у складу са Законом о општем управном поступку као посебном закону који уређује надлежност, састав суда и правила поступка на основу којих управни судови одлучују о законитости одлука јавноправних тела о правима, обавезама и правним интересима физичких и правних особа и других странака, о законитости поступања јавноправних тела из подручја управног права (управни спор).','Руководилац службе правних послова','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:42');
INSERT INTO `FUK_AKTIVNOST` VALUES (3187,3013,3017,'Контрола',NULL,4,'','Контролише се вођење управног спора.','Извршни директор за правно-економске послове','',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:42');
