INSERT INTO `FUK_AKTIVNOST` VALUES (3190,3013,3018,'Захтев за покретање поступка',NULL,2,'Захтеви','Самостални стручни сарадник  правних послова  прима захтеве за покретање поступака код надлежног суда па се у том стадијуму предузимају активности у циљу претходног испитивања поднесене тужбе или се врше активности око подизања тужбе.','Руководилац службе правних послова','Према потреби',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:43');
INSERT INTO `FUK_AKTIVNOST` VALUES (3191,3013,3018,'Разматрање тужби или предлог подизања тужби',NULL,3,'Тужбе, Предлози тужби','Примљене тужбе или предлоге око подизања тужби разматра Самостални стручни сарадник  правних послова  , поступа по њима или додељује  на решавање.','Руководилац службе правних послова','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:43');
INSERT INTO `FUK_AKTIVNOST` VALUES (3192,3013,3018,'Додела предмета',NULL,4,'Предмет','Тужбу или предлог за подизање тужбе додељује по потреби адвокатима.','Руководилац службе за правне послове','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:43');
INSERT INTO `FUK_AKTIVNOST` VALUES (3193,3013,3018,'Испитивање постојања правозаштитне потребе',NULL,5,'','Радње које се унутар ЈКП „Медиана“-Ниш током општинског судског поступка предузимају регулисани су правним правилима, законом, а заштиту у правилу пружају државни органи правосуђа.Прва радња је испитивање постојања правозаштитне потребе тј.испитивање да ли је потребна интервенција суда ради заштите и остваривања права, односно  тражи се правни интерес као процесна претпоставка за допустивост истицања захтева за пружање правне заштите.Процесне претпоставке квалификују се као околности без чијег постојања покретање парнице није допуштено, па се испитују: претпоставке које се тичу суда- којем суду поднети тужбу (судска надлежност, стварна и месна надлежност), претпоставке које се тичу странака (страначка способност, парнична способност странака, правни интерес за тражење правне заштите одређеног садржаја), претпоставке које се тичу....','Самостални стручни сарадник  правних послова','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:43');
INSERT INTO `FUK_AKTIVNOST` VALUES (3194,3013,3018,'Подношење тужбе',NULL,7,'Тужба','Тужба је једна од парничних радњи искључиво резервисана за тужиоца, па када се ПУ нађе у тој страначкој улози, тужбом ће покренути парнични поступак пред надлежним судом.За утврђење садржаја тужбе меродавне су опште одредбе о садржају подношења, опште одредбе о садржају тужбе,  посебне одредбе о садржају неких врста тужби.Тужба се подноси надлежном суду, па једном покренут тужбом парнични поступак који означава редовни пут правне заштите у подручју грађанскоправних односа, одвија се даље по тачно утврђеним законским правилима све док првостепени суд не донесе коначну одлуку.','Самостални стручни сарадник  правних послова','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:43');
INSERT INTO `FUK_AKTIVNOST` VALUES (3195,3013,3018,'Претходно испитивање тужбе',NULL,8,'Тужба','Претходно испитивање тужбе обавезан је елемент главне расправе.нпр.Да би се утврдило да ли тужба садржи све нужне елементе које тужба мора имати нпр.нема тужбеног захтева, да ли је суд надлежан.','Самостални стручни сарадник  правних послова','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:43');
INSERT INTO `FUK_AKTIVNOST` VALUES (3196,3013,3018,'Припремно рочиште',NULL,9,'','На припремном рочишту странке су дужне изнети све чињенице и доказе, ако то не учини сноси парничне трошкове  изазване противној странци, осим ако потреба за изношењем нових доказа и чињеница није изазвана понашањем противне странке.','Руководилац службе за правне послове','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:43');
INSERT INTO `FUK_AKTIVNOST` VALUES (3197,3013,3018,'Достављање тужбе туженом на одговор',NULL,10,'','Доставља се туженом тужба на одговор ради подношења писаног одговора на тужбу а последице не одговарања  јесу доношење пресуде због пропуштања.','Оптужени','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:44');
INSERT INTO `FUK_AKTIVNOST` VALUES (3198,3013,3018,'Заказивање рочишта/главне расправе',NULL,11,'','Суд одређује рочишта кад је то законом прописано или кад то захтевају потребе поступка, на којима суд предузима парничне радње уз суделовање странака и других учесника у поступку, ради правилне примене правних правила на правне односе што зависи од потпуног и правилног утврђивања чињеничног стања у спору.Доказивање  представља  низ парничних радњи процесних субјеката  које се састоје у предлагању, избору, прикупљању, испитивању и оцењивању доказних средстава ради утврђивања истинитости тврдњи парничних странака и претпоставки суда о чињеницама које су битне за примену права.','Надлежни суд','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:44');
INSERT INTO `FUK_AKTIVNOST` VALUES (3199,3013,3018,'Главна расправа',NULL,12,'','Главна расправа је основни стадијум парничног поступка у коме се на посебном рочишту организованом на начелима усмености, јавности, непосредности и  контрадикторности прикупља и испитује процесна грађа и расправља о основаности захтева које су странке истакле током парнице.На главној расправи суд мора одлучити и о свим питањима процесне нарави ако нису решена у стадијуму припремања главне расправе.Главна расправа може се одржати само ако суд уредним достављањем  позива, омогући странкама учествовање на расправи.Ако изостане  само једна странка, а друга својим држањем не доведе до мировања поступка или до доношења пресуде због изостанка,  расправа се може  одржати с присутном странком.Ток главне расправе законом је фиксиран у најосновнијим цртама, па зависи од суда које ће радње предузимати.','Надлежни суд','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:44');
INSERT INTO `FUK_AKTIVNOST` VALUES (3200,3013,3018,'Доношење пресуде',NULL,13,'Пресуда','Након закључења главне расправе судија доноси пресуду и јавно је објављује.','Надлежни суд','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:44');
INSERT INTO `FUK_AKTIVNOST` VALUES (3201,3013,3018,'Поступак подношења Жалбе',NULL,14,'ПресудаЖалба','Првостепена пресуда којом је одлучено о предмету спора може се жалбом побијати због неправилности које је суд починио у поступку њеног доношења.У парничном стадијуму покренутом жалбом  против пресуде првостепеног суда учествују два суда; првостепени  суд који је донео нападану пресуду и коме се жалба подноси, и другостепени суд надлежан да одлучује о жалби – надлежни суд.Првостепени суд прима жалбу и одлучује да ли је подобна за мериторно разматрање.Другостепени суд одлучује о основаности жалбе и он може и мора одлучивати је ли подобна за мериторно разматрање ако је то првостепени суд пропустио учинити или је о томе погрешно одлучио.Жалба се подноси суду који је изрекао првостепену пресуду, у довољном броју примерака за суд и противника.','Надлежни суд','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:44');
INSERT INTO `FUK_AKTIVNOST` VALUES (3202,3013,3018,'Одлука другостепеног суда',NULL,15,'Одлука','Другостепени суд може донети следеће одлуке:1) одбаци жалбу као неблаговремену, непотпуну или као недозвољену;2) одбије жалбу као неосновану и потврди првостепену пресуду;3) укине пресуду и упути предмет првостепеном суду на поновно суђење;4) укине првостепену пресуду и одбаци тужбу;5) преиначи првостепену пресуду и одлучи о захтевима странака;6) усвоји жалбу, укине пресуду и одлучи о захтевима странака.','Другостепени суд','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:44');
INSERT INTO `FUK_AKTIVNOST` VALUES (3203,3013,3018,'Примање одлуке',NULL,16,'Одлука','Одлука другостепеног суда прима се преко улазне поште.','Другостепени суд','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:44');
INSERT INTO `FUK_AKTIVNOST` VALUES (3204,3013,3018,'Вадредни правни лек',NULL,17,'','Понављање поступкаи ревизија','Другостепени суд','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:44');
INSERT INTO `FUK_AKTIVNOST` VALUES (3205,3013,3018,'Спровођење ревизије',NULL,18,'Члан 407, Закон о парничном поступку','Ревизија против другостепене пресуде може се поднети:1) битне повреде одредаба парничног поступка из члана 374.став 2.тачка 2) овог закона; 2) битне повреде одредаба парничног поступка из члана 374.став 2.тач.6), 8), 10) и 11) овог закона, под условом да су истицане у жалби, односно да су учињене у поступку пред другостепеним судом; 3) битне повреде одредаба парничног поступка из члана 374.став 1.овог закона која је учињена у поступку пред другостепеним судом; 4) погрешне примене материјалног права;5) прекорачења тужбеног захтева само ако је та повреда учињена у поступку пред другостепеним судом.','Другостепени суд','У року од30 дана од дана доставе другостепене пресуде',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:44');
INSERT INTO `FUK_AKTIVNOST` VALUES (3206,3013,3018,'Понављање поступка',NULL,19,'Члан 426,Закон о парничном поступку','Понављање поступка је ванредни правни лек против одлука којима је поступак суда правоснажно завршен.Поступак који је одлуком суда правноснажно окончан може да се по предлогу странке понови ако:1) је суд био непрописно састављен или ако је судио судија који је по закону морао да буде искључен или је решењем суда био изузет или ако је у доношењу пресуде учествовао судија који није учествовао на главној расправи;2) странци незаконитим поступањем, а нарочито пропуштањем достављања, није било омогућено да расправља пред судом;','Другостепени суд','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:44');
INSERT INTO `FUK_AKTIVNOST` VALUES (3207,3013,3018,'Одлука уставног суда',NULL,22,'Одлука','Уставни суд доноси одлуку.','Уставни суд','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:45');
INSERT INTO `FUK_AKTIVNOST` VALUES (3208,3013,3018,'Поступак подношења жалби',NULL,23,'','Незадовољна страна одлуком Уставног суда подноси жалбу Европском суду.','Незадовољна странка','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:45');
