INSERT INTO `FUK_AKTIVNOST` VALUES (3261,3020,3025,'Евидентирање             возила на          улазној рампи',NULL,2,'Дневни извештај РЈ Депонија Записник о одбијању депоновањаДневна евиденција за трећа лица','При доласку возила на градску депонију Бубањ,врши се идентификација ,евидентирање броја извежених тура и количина изнетог смећа на депонији возилима правних и физичких лица. Приспеле количине и врсте отпада, контролор депоновања отпада евидентира у дневној евиденцији за правна и физичка лица . Уколико приликом контроле установи да се ради о отпаду чије збрињавање није дозвољено (опасан отпад, посебни токови отпада, сортирани амбалажни отпад и сл.), забрањује се пријем истог на телу депонијe и формира записник о одбијању депоновања са  подацима (o разлогу забране депоновања, као и о процењеној врсти и количини отпада).','Контролор депоновања смећаВозач','одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:50');
INSERT INTO `FUK_AKTIVNOST` VALUES (3262,3020,3025,'Мерење пуног          возила',NULL,3,'Мернина-пријемница','Након доласка корисника , исказана је потреба за депоновање корисник је дужан да се измери на колској ваги пун и касније празан и добија се докуменат мернина – пријемница бр xxx..','Контролор депоновања смећаВозач','одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:50');
INSERT INTO `FUK_AKTIVNOST` VALUES (3263,3020,3025,'Истовар возила',NULL,4,'Дневни  план одлагања отпадаПлан дневног ангажовања радне снаге и машина','Правна и физичка лица остварују право да у складу са важећом Одлуком о ценама услуга на депонији, депонују на градској депонији Бубањ Пословођа прати и усмерава отпадни материјал до радног чела депоније. Прати и издаје наређења руковаоцима радним машинама за формирање и одржавање.интерне саобраћајницеРуковалац радним машинама координира возила која допремају отпад,  реализује технологију одлагања отпада на планираној касети сегмента поља депоније. Технологија одлагања отпада састоји се од разастирања и компактирања отпада, као и прекривања инертним материјалом.','Пословођа на депоновању отпада,Руковалац радном машином,Возач','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:50');
INSERT INTO `FUK_AKTIVNOST` VALUES (3264,3020,3025,'Мерење        празног возила',NULL,5,'Дневни извештај РЈ Депонија,Мернина-пријемница','По завршетку депоновања отпада,врши се мерење возила трећих лица','Контролор депоновања отпада','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:50');
