INSERT INTO `FUK_AKTIVNOST` VALUES (3266,3020,3025,'Наплата           истоварног              отпада',NULL,7,'Потврда о извршеној услузи за правна лица,Фискални рачун за физичка лица Референт за фактурисање прихода','Након реализације услуге, контролор депоновања отпада сачињава Потврду о извршеној услузи. Потврда се израђује у 4 примерка, од чега се 1. примерак издаје кориснику услуге, 2. примерка се достављају Служби пореза и цена на фактурисање, док  1. примерак остаје у РЈ „Депонија','Контролор депоновања отпада','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:50');
INSERT INTO `FUK_AKTIVNOST` VALUES (3267,3020,3025,'Евиденција        безготовинске         и готовинске              наплате',NULL,8,'Евиденција наплате прихода,Благајнички дневник','Након наплате услуге прослеђује се документација   финансијској служби','Пословођа депоновања отпада,Контролор депоновања отпада','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:50');
INSERT INTO `FUK_AKTIVNOST` VALUES (3268,3020,3025,'Извештавање',NULL,9,'Месечни извештаји,Годишњи извештај РЈ Депонија','Руководилац РЈ „Депонија“ израђује месечни и годишњи извештај  о раду РЈ Депонија, који садржи: финансијски износ реализације услуге за правна и физичка лица, количине депонованог комуналног отпада од стране ЈКП „Медиана“ Ниш, количине друге врсте отпада од правних и физичких У складу са Правилник о обрасцу дневне евиденције и годишњег извештаја о отпаду са упутством за његово попуњавање (\"Службени гласник РС\", број 95/2010) и Правилник о изменама и допунама Правилника о обрасцу дневне евиденције и годишњег извештаја о отпаду са упутством за његово попуњавање (\"Службени гласник РС\" број 88/2015), РЈ „Депонија“ води дневне евиденције о отпаду на обрасцу ДЕО 2 који се доставља Центру за заштиту животне средине и одрживи развој.лица, као и подаци о експлоатацији возила.','Руководилац РЈ ДепонијаЦентар за ЗЖС и одрживи развој','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:50');
