INSERT INTO `FUK_AKTIVNOST` VALUES (3316,3026,3031,'Именовање радне групе',NULL,3,'Именовање радне групе','Именује се радна група (Комисија за израду дугорочног и средњорочног плана пословне стратегије и развоја )за израду планских докумената.','Директор','Година у којој истиче предходно усвојени план',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:55');
INSERT INTO `FUK_AKTIVNOST` VALUES (3317,3026,3031,'Дефинисање мисије,визије и циљева',NULL,4,'Изјава о визији,мисији,циљевима','На састанку радна група расправља о дефинисању мисије,визије и кључних циљева јавног предузећа.На бази састанка радне групе саставља се изјава о мисији,визији и кључним циљевима јавног предузећа заједно са планом потребних активности и мера за састављање планских докумената','Чланови радне  групе','Током радног састанка .',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:55');
INSERT INTO `FUK_AKTIVNOST` VALUES (3318,3026,3031,'Приоритета,мера и активности',NULL,5,'','Дефинишу се сви приоритети,мере и активности за израду дугорочног и средњорочног плана пословне стратегије и развоја','Чланови радне  групе','Током радног састанка',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:55');
INSERT INTO `FUK_AKTIVNOST` VALUES (3319,3026,3031,'НЕ                                              ДАКонтрола',NULL,6,'Изјава о визији,мисији,циљевима','Контролише се садржај изјаве о мисији,визији и кључним циљевима као и нацрта активности и мера.Уколико се утврде неправилности и одступања приступа се новом састанку.','Директор','Након састављене Изјаве',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:55');
INSERT INTO `FUK_AKTIVNOST` VALUES (3320,3026,3031,'Распоређивање  и извршењерадних задатака',NULL,7,'Распоред радних задатака','Обавља се распоред радних задатака унутар радне групе на бази утврђених активности за израду дугорочног и средњорочног плана пословне стратегије и развоја.У складу са распоредом, извршавају се радни задаци за израду планских  докумената.','Директор.','Након састављене Изјаве',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:55');
INSERT INTO `FUK_AKTIVNOST` VALUES (3321,3026,3031,'Писање SWOT анализе',NULL,8,'SWOT анализа','Саставља се SWOT анализа за израду дугорочног и средњорочног плана пословне стратегије и развоја.','Чланови радне групе','Након састављене Изјаве',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:55');
INSERT INTO `FUK_AKTIVNOST` VALUES (3322,3026,3031,'Писање предлога и мера',NULL,9,'SWOT анализа','Састављају се све мере и предлози у изради планских аката.Израђује се коначна верзија планских докумената.','Чланови радне групе','Након састављене Изјаве',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:55');
