INSERT INTO `FUK_AKTIVNOST` VALUES (3325,3026,3031,'Усвајање планских докумената',NULL,12,'Планска докумената','Директор  усваја или не усваја презентовану израду средњорочног дугорочног плана пословне стратегије и развојаУколико се  не усвоји планска акта приступа се поновном извршавању радних задатака и изради новог дугорочног и средњорочног плана пословне стратегије и развоја','Директор','Година у којој истиче предходно усвојени план',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:55');
