INSERT INTO `FUK_AKTIVNOST` VALUES (3330,3027,3032,'Праћење законских прописа',NULL,2,'Образац Попис законске/ подзаконске регулативе','Праћење информација о законским и подзаконским прописима који су примењиви на нашу делатност. Прате се закони на wеб страници Министарства финансија и wеб страници Службени гласник.','Руководилац службе правних послова','Континуирано',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:56');
INSERT INTO `FUK_AKTIVNOST` VALUES (3331,3027,3032,'Контрола',NULL,3,'Образац Попис законске/ подзаконске регулативе','Упоређују се измене и допуне закона, уредби и прописа са постојећим правним актима. Уколико нема измена иде се на даље праћење аката, уколико постоје измене о томе се обавештава надлежни Руководилац службе.','Руководилац службе правних послова','Континуирано',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:56');
INSERT INTO `FUK_AKTIVNOST` VALUES (3332,3027,3032,'Обавештење  надлежним Руководиоцима служби',NULL,4,'Обавештење о изменама','Контактира се надлежни Руководилац службе електронским путем и телефонски и обавештава о изменама.','Руководилац службе правних послова','Одмах приликом уочавања измена',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:56');
INSERT INTO `FUK_AKTIVNOST` VALUES (3333,3027,3032,'Предлагање решења',NULL,5,'','Руководилац надлежне службе предлаже решење директору о изменама документације, начина рада, о променама у пословању, евентуалном обавештавању корисника услуга и сл.','Руководилац  надлежне службе','Пре ступања на снагу прописа',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:56');
INSERT INTO `FUK_AKTIVNOST` VALUES (3334,3027,3032,'Одлука',NULL,6,'Одлука о даљем поступању','Извршни директор за правно-економске послове доноси одлуку о даљем поступању и о томе обавештава Руководиоца службе правних послова.','Извршни     директор за правно-економске послове','Пре ступања на снагу прописа',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:56');
INSERT INTO `FUK_AKTIVNOST` VALUES (3335,3027,3032,'Обавештење руководиоцу службе',NULL,7,'Обавештење о одлуци','Руководилац службе правних послова обавештава Руководиоце надлежне службе и остале Руководиоце служби телефонским путем и електронском поштом.','Руководилац службе правних послова','Пре ступања на снагу прописа',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:56');
INSERT INTO `FUK_AKTIVNOST` VALUES (3336,3027,3032,'Обавештење запослених',NULL,8,'Обавештење о одлуци','Руководиоци служби о одлуци обавештавају запослене у својој служби.','Руководиоци служби','Пре ступања на снагу прописа',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:56');
INSERT INTO `FUK_AKTIVNOST` VALUES (3337,3027,3032,'Обавештење кориснику услуге',NULL,9,'Обавештење о одлуци','Уколико постоји потреба за тим, корисници услуге се морају обавестити о даљем начину рада или о изменама закона, Руководилац службе  правних послова организује начин контактирања корисника услуге.','Руководилац службе правних послова','Пре ступања на снагу прописа',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:38:56');
