INSERT INTO `FUK_AKTIVNOST` VALUES (3409,3032,3037,'Пријава           неправилности',NULL,2,'','Достављање информације у вези са унутрашњим узбуњивањем врши се лицу овлашћеном за пријем информације и вођење поступка у вези са унутрашњим узбуњивањем, писмено или усмено. Поступа се у складу са Законом о заштити узбуњивача и Правилником о начину унутрашњег узбуњивања у организацији.','Узбуњивач','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:07');
INSERT INTO `FUK_AKTIVNOST` VALUES (3410,3032,3037,'Пријава          неправилности',NULL,3,'','Достављање информације у вези са унутрашњим узбуњивањем врши се лицу овлашћеном за пријем информације и вођење поступка у вези са унутрашњим узбуњивањем, писмено или усмено. Поступа се у складу са Законом о заштити узбуњивача и Правилником о начину унутрашњег узбуњивања у организацији.','Узбуњивач','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:07');
INSERT INTO `FUK_AKTIVNOST` VALUES (3411,3032,3037,'Анонимна                 пријава',NULL,4,'','У случају анонимних обавештења, поступа се у складу са Законом.','Узбуњивач','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:07');
INSERT INTO `FUK_AKTIVNOST` VALUES (3412,3032,3037,'Писмено            достављање            информације',NULL,5,'Потврда о пријему информације у вези са унутрашњим узбуњивањем','Писмено достављање информације у вези са унутрашњим узбуњивањем може се учинити непосредном предајом писмена о информацији у вези са унутрашњим узбуњивањем, обичном или препорученом пошиљком, као и електронском поштом, на e-mail Овлашћеног лица:sanja.jefic@jkpmediana.rs,у складу са законом, уколико постоје техничке могућности,o чему се саставља и издаје потврда.Потврда о пријему информације у вези са унутрашњим узбуњивањем садржи: кратак опис чињеничног стања о информацији у вези са унутрашњим узбуњивањем; време, место и начин достављања информације у вези са унутрашњим узбуњивањем; број и опис прилога поднетих уз информацију о унутрашњем узбуњивању','Узбуњивач','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:07');
INSERT INTO `FUK_AKTIVNOST` VALUES (3413,3032,3037,'Писмено             достављање            информације',NULL,6,'','... податке о томе да ли узбуњивач жели да подаци о његовом идентитету не буду откривени; податке о послодавцу; печат послодавца; потпис лица овлашћеног за пријем информације и вођење поступка у вези са унутрашњим узбуњивањем.','Узбуњивач','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:07');
INSERT INTO `FUK_AKTIVNOST` VALUES (3414,3032,3037,'Усмено             достављање            информације',NULL,7,'Потврда о пријему информације у вези са унутрашњим узбуњивањем','Усмено достављање информације у вези са унутрашњим узбуњивањем врши се усмено на записник. Уколико се достављање информације врши непосредном предајом писмена или усмено, о информацији у вези са унутрашњим узбуњивањем саставља се потврда о пријему информације у вези са унутрашњим узбуњивањем, и то приликом пријема писмена, односно приликом узимања усмене изјаве од узбуњивача.','Узбуњивач','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:07');
INSERT INTO `FUK_AKTIVNOST` VALUES (3415,3032,3037,'Провера            информација',NULL,8,'Записник, Приговор','У циљу провере информације у вези са унутрашњим узбуњивањем, предузимају се одговарајуће радње, о чему се обавештава послодавац, као и узбуњивач, уколико је то могуће на основу расположивих података. Ако се узимају изјаве од лица, у циљу провере информације у вези са унутрашњим узбуњивањем, о томе се саставља записник. На садржину записника, може се ставити приговор.','Овлашћено лице','Најкасније у року од 15 дана',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:07');
INSERT INTO `FUK_AKTIVNOST` VALUES (3416,3032,3037,'Извештавање            послодавца и              узбуњивача',NULL,9,'Извештај о предузетим радњама у поступку о информацији у вези са унутрашњим узбуњивањем','По окончању поступка саставља се извештај о предузетим радњама у поступку о информацији у вези са унутрашњим узбуњивањем, предлажу мере ради отклањања уочених неправилности и последица штетне радње насталих у вези са унутрашњим узбуњивањем.Извештај из става 1. овог члана, доставља се послодавцу и узбуњивачу, о коме се узбуњивач може изјаснити у року од 8 дана.Ради отклањања уочених неправилности и последица штетне радње настале у вези са унутрашњим узбуњивањем могу да се предузму одговарајуће мере на основу извештаја.','Овлашћено лице','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:07');
INSERT INTO `FUK_AKTIVNOST` VALUES (3417,3032,3037,'Предузимање                       мера',NULL,10,'Извештај о предузетим радњама у поступку о информацији у вези са унутрашњим узбуњивањем','Ради отклањања уочених неправилности и последица штетне радње настале у вези са унутрашњим узбуњивањем могу да се предузму одговарајуће мере на основу извештаја.','Директор','У року од 15 дана',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:07');
