INSERT INTO `FUK_AKTIVNOST` VALUES (3436,3033,3038,'Евиденцијапроговора',NULL,18,'Грађевински дневник','Рекламацију на рад службе евидентира надзорни орган кроз грађевински дневник о евидентираним рекламацијама Администратор РЈ обавештава службу контакт центар која има задатак да се бави обрадом свих приговора.','Администратор РЈНадзорни органКоординатор контакт центар','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:09');
