INSERT INTO `FUK_AKTIVNOST` VALUES (3453,3036,3041,'Пријем приговора,жалбе,рекламације',NULL,2,'Допис правног субјекта или грађана,Потврда о пријему','Корисник услуга има право на подношење рекламације на извршене услуге у складу са Законом о заштити потрошача и Правилником. Корисник је дужан да плати неспоран део износа рачуна.Рекламације се примају:- усмено, путем телефона или непосредно,- у писаном облику, сачињене на рекламационом записнику или писменом обрасцу сачињеном у слободној форми, од стране купца/корисника услуге.- у електронској форми, путем сајта ЈКП „Медиана“-Ниш (www.mediana. rs), слањем емаил-а директно са контакт форме која се налази на сајту. Запослени одређен за пријем рекламација је обавезан да странци која лично предаје рекламацију изда потврду о пријему.','Самостални стручни сарадник за правне послове','Један дан',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (3454,3036,3041,'Обрада приговора рекламације',NULL,3,'Допис правног субјекта или грађана,','Самостални стручни сарадник за правне послове примљене рекламације уноси у Књигу рекламација која се води у писменој форми. Усмене рекламације се такоде евидентирају у “Књигу рекламација”, са напоменом да је то усмена рекламација - приговор купца. Рекламације примљене електронским путем такође се евидентирају у Књигу рекламација и снимају на хард диск рачунара.','Самостални стручни сарадник за правне послове','Један дан',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:11');
