INSERT INTO `FUK_AKTIVNOST` VALUES (3469,3038,3043,'Евидентирање       возила на узлазној                   рампи',NULL,2,'Дневни извештај РЈ ДепонијаЕвиденција долазака возила на депонију','При доласку возила на градску депонију Бубањ, контролор депоновања отпада врши идентификацију, доласка/одласка уписује име возача и број радникадоласка , евидентира број тура , количину и врсту приспелог отпада.','Контролор депоновања смећаВозач,Пословођа на депоновању отпада','одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:12');
