INSERT INTO `FUK_AKTIVNOST` VALUES (3472,3038,3043,'Истовар возила',NULL,5,'План дневног ангажовања радне снаге и машина,Дневни план одлагања отпада,Радни налог','У складу са Планом дневног ангажовања радне снаге и машина  и  Дневним планом одлагања отпада, према налогу руководиоца РЈ „Депонија“, пословођа на депоновању отпада и руковалац радном машином, реализују технологију одлагања отпада на планираној касети сегмента поља депоније. Технологија одлагања отпада састоји се од разастирања и компактирања отпада, као и прекривања инертним материјалом. Технологију одлагања спроводи руковалац радном машином а према радном налогу за возило.','Пословођа депоновања отпада,Руковалац радном машином,Возач','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:13');
INSERT INTO `FUK_AKTIVNOST` VALUES (3473,3038,3043,'',NULL,6,'Месечни извештај Р.Ј.Депонија“,Годишњи извештај Р.Ј.Депонија“','Руководилац РЈ„Депонија“израђује месечни  и годишњи извештај   о раду РЈ „Депонија“,којисадржи: финансијски износ реализације услуге за правна и физичка лица, количине депонованог комуналног отпада од стране ЈКП „Медиана“ Ниш, количине друге врсте отпада од правних и физичких лица, као и подаци о експлоатацији возила. У складу са Правилник о обрасцу дневне евиденције и годишњег извештаја о отпаду са упутством за његово попуњавање (\"Службени гласник РС\", број 95/2010) и Правилник о изменама и допунама Правилника о обрасцу дневне евиденције и годишњег извештаја о отпаду са упутством за његово попуњавање (\"Службени гласник РС\" број 88/2015), РЈ „Депонија“ води дневне евиденције о отпаду на обрасцу ДЕО 2 који се доставља Центру за заштиту животне средине и одрживи развој.','Руководилац РЈ Депонија“, Центар за ЗЖС и одрживи развој','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:13');
