INSERT INTO `FUK_AKTIVNOST` VALUES (3491,3040,3045,'Унос података годишње евиденције',NULL,4,'ГИО1, ГИО2КОМ','На интернет порталу Агенције за заштиту животне средине уносе се параметри везани за годишње управљање отпадом и то за:- генерисани отпад,- одложени отпад и- комунални отпад','Инжењер за мониторинг и извештавање','90 дана(од почетка нове календарске године за претходну)',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:14');
