INSERT INTO `FUK_AKTIVNOST` VALUES (3521,3043,3048,'Израда месечног   плана рада',NULL,2,'Месечни план радапрања улица','На основу решења о програму одржавања јавне хигијене израђује се месечни план рада','Директор сектора одржавања хигијенеРуководилац РЈ за уклањање отпада и прање јавних површина/“Зимска служба','До 25-ог у текућем месецу за наредни месец',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:18');
