INSERT INTO `FUK_AKTIVNOST` VALUES (3525,3043,3048,'Одлазак на терен    и прање улица',NULL,6,'Радни налогПутни налог','Долазак радника у седиште предузећа и преузимање алата потребног за прање јавних површина водом уз помоћ цистерни.Пословођа на терену контролише примену ЛЗО и мера за безбедан и здрав рад.','.Пословођа РЈРадник на прању','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:18');
INSERT INTO `FUK_AKTIVNOST` VALUES (3526,3043,3048,'По завршеткупосла повратак уседиште предузећа',NULL,7,'Радни налогПутни налог','По извршењу активности на терену оперативци попуњавају радне и путне налоге.Радни налози садрже податке о обиму и врсти извршених послова.Радници на прању су у обавези да врате алат.','Пословођа РЈРадник на прању','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:18');
INSERT INTO `FUK_AKTIVNOST` VALUES (3527,3043,3048,'Надзор на	  	реализованим    радовима',NULL,8,'Грађевински дневникГрађевинска књигаМесечна фин.ситуација','Руководилац РЈ грађевинску књигу и грађевински дневник потписује у име извршиоца.Грађевинску књигу и грађевински дневник оверава надзорни орган именован од стране скупштине града-Дирекција за изградњу града.На основу потписаних ситуација од стране надзорног органа попуњава се  формулар месечна финансијска ситуација.','Руководилац РЈНадзорни органАдминистратор РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:18');
