INSERT INTO `FUK_AKTIVNOST` VALUES (3529,3043,3048,'Евиденцијаприговора',NULL,10,'Грађевински дневник','Рекламацију на рад службе евидентира надзорни орган кроз грађевински дневник о евидентираним рекламацијама. администратор РЈ обавештава службу контакт центар','Администратор РЈНадзорни орган','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:18');
INSERT INTO `FUK_AKTIVNOST` VALUES (3530,3043,3048,'Извештавање',NULL,11,'Обрасци реализације послова,Дневни извештај о раду,Извештај о раду','Руководилац РЈ попуњава дневни извештај о раду који се односи на радну снагу и формулар који се односи на извршене послове и стање пратеће опреме (возила),у који уноси евидентиране рекламације од стране надзорног органа.Дневни извештаји о раду и учинку се доставља Директору сектора одржавања хигијене.','Директор сектора одржавања хигијенеРуководилац РЈ Администратор РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:18');
