INSERT INTO `FUK_AKTIVNOST` VALUES (3533,3044,3049,'Израдамесечног планарада',NULL,2,'Месечни план одржавања јавних тоалета','На основу решења о програму одржавања јавне хигијене израђује се месечни план рада одржавање јавних тоалета','Директор сектора одржавања хигијенеРуководилац РЈ за уклањање отпада и прање јавних површина/“Зимска служба','До 25-ог у текућем месецу за наредни месец',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:19');
INSERT INTO `FUK_AKTIVNOST` VALUES (3534,3044,3049,'Израдаоперативногплана рада',NULL,3,'Недељни план рада чишћења ЈП и одржавања ЈТДневни распоред,Евиденција рада треће смене','На основу месечног плана рада по програму одржавања јавне хигијене израђује се оперативни план на дневном и недељном нивоу.Недељни план рада за ручно чишћење јавних површина, израђује руководилац РЈ „Чишћење јавних површина и одржавање јавних тоалета“, а одобрава директор Сектора одржавања хигијене. Дневни план израђује администратор РЈ „Чишћење јавних површина и одржавање јавних тоалета“ на формулару дневни распоред  и Евиденција рада треће смене , а оверава га координатор. Одобрени недељни план рада се доставља пословођама РЈ ради организације екипа  на извршењу ручног и машинског чишћења улица и одржавања хигијене јавних тоалета.','Администратор РЈ','2 дана',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:19');
INSERT INTO `FUK_AKTIVNOST` VALUES (3535,3044,3049,'Припрема зареализацијупланова',NULL,4,'Радне листе','На основу израђених планова на недељном и дневном нивоу,руководилац РЈ,Координатор РЈ и пословођа израђују радне листе ,након чега координатор даје усмени налог пословођама и радницима на којим реонима се ради.','Руководилац РЈКоординатор РЈПословођа РЈ','1 дан',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:19');
