INSERT INTO `FUK_AKTIVNOST` VALUES (3537,3044,3049,'Одржавањехигијене у јавнимтоалетима',NULL,6,'Радне листе','Пословођа РЈ са комуналним радницима излази на терен и  по плану рада организује активности одржавања хигијене јавних тоалета по реонима. Комунални радник на одржавању хигијене у јавним тоалетима одговоран је одржава,чисти улазна врата  и простор око ВЦ-а  .','.Пословођа РЈКомунални радник','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:19');
INSERT INTO `FUK_AKTIVNOST` VALUES (3538,3044,3049,'Евиденција              приговора',NULL,7,'Евиденција приговора','Администратор  евидентира све приговоре у образац  – евиденција приговора у РЈ «Одржавање јавних површина и јавних тоалета». Руководилац РЈ  предузима одговарајуће мере за решавање приговора. О евидентираним приговорима администратор РЈ обавештава службу «контакт центар» која има задатак да се бави обрадом свих приговора','Администратор','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:19');
INSERT INTO `FUK_AKTIVNOST` VALUES (3539,3044,3049,'Извештај',NULL,8,'Радни налог','Шеф РЈ за чишћење јавних површина и одржавање јавних тоалета попуњава извештај о раду и доставља директору сектора Јавна хигијена.','Директор сектора Јавна хигијена,Шеф РЈ','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:19');
