INSERT INTO `FUK_AKTIVNOST` VALUES (3547,3046,3051,'Израда                 месечног               плана рада',NULL,2,'Месечни план сакупљања отпада','На основу решења о Програму сакупљања отпада и прања типских посуда (контејнера) израђује се месечни план рада','Директор сектора Управљања отпадомРуководилац РЈ Координатор РЈ','До 25-ог у текућем месецу за наредни месец',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:20');
INSERT INTO `FUK_AKTIVNOST` VALUES (3548,3046,3051,'Израда              оперативног              плана рада',NULL,3,'Недељни план рада сакупљања отпада','На основу месечног плана рада по Програму сакупљања отпада и прања типских посуда (контејнера ) израђује се оперативни план на дневном и недељном нивоу.','Директор сектора Управљања отпадомРуководилац РЈ Координатор РЈ Администратор РЈ','2 дана',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:20');
INSERT INTO `FUK_AKTIVNOST` VALUES (3549,3046,3051,'Планирање',NULL,4,'План рада запосленихДневни распоред запосленихРадна  листа за контејнереПутни налог','На основу израђених планова на недељном и дневном нивоу,руководилац РЈ,Координатор РЈ и пословођа израђују радне листе,радне и путне налоге и тахографски листић,након чега координатор даје усмени налог пословођама и радницима на којим реонима се ради.Пословођа издаје возачу посаде возила радну листу за контејнере.','Руководилац РЈКоординатор РЈПословођа на сакупљању и транспорту отпада','1 дан',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:20');
INSERT INTO `FUK_AKTIVNOST` VALUES (3550,3046,3051,'Формирање                  екипа',NULL,5,'','Пословођа на сакупљању и транспорту отпада,врши формирање екипа за рад на реонима и то:У случају сакупљања кабастог отпада на јавним површинама, рејон-екипа се састоји из 3 члана (возач и 2 радника које раде на утовару отпада. На реон може да изађе екипа и са 1 радником.','Пословођа на сакупљању и транспорту отпада','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:20');
