INSERT INTO `FUK_AKTIVNOST` VALUES (3552,3046,3051,'Пријем возила            од стране             возача у           контролној             служби',NULL,7,'Радни налогПутни налог','Долазак радника у седиште предузећа и преузимање  возила од контролне службе, потребног за сакупљање неопасног отпада и транспортна депонију  на основу радне листе и усменог налога извршавају описане активности..Пословођа на терену контролише извршење радова по реонима.','.Пословођа РЈВозач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (3553,3046,3051,'Визуелна провера   техничке исправности               возила',NULL,8,'Путни налог','Визуелна провера техничке исправности возила од стране возача','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (3554,3046,3051,'Преузимање            радника на       прозивном месту',NULL,9,'Радна листа за контејнереПутни налог','Долазак на прозивно место возача са возилом','ВозачКомунални радници','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (3555,3046,3051,'Сакупљање и      транспорт кабастог      комуналног отпада           шута и разног           другог отпада',NULL,10,'Радна листа за контејнереПутни налог','Извршење активности на реону  на основу радне листе и усменог налога оперативци  извршавају описанеактивности.Пословођа на терену контролише извршење радова по реонима. У случају промене локације и промене броја контејнера, која се уписује у радној  листи за контејнере, измена се евидентира у евиденцији локација контејнера  и о томе се обавештава Служба гис/гпс достављањем нове евиденције контејнера','Пословођа на сакупљању и транспорту комуналног отпадаВозачКомунални радник','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (3556,3046,3051,'Евентуална         комуникација са              пословођом',NULL,11,'Путни налогКомуникација путем мобилних телефона','Комуникација возача са пословођом на терену путем мобилних телефона у случају застоја у раду (квара на возилу и сл.)','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (3557,3046,3051,'Улазак возила на              депонију',NULL,12,'Путни налогЕвиденција доласка возила','Врши се евиденција доласка возила РЈ сакупљања и транспорта отпада која довозе кабасти отпад..','ВозачКонтролор депоновања отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (3558,3046,3051,'Истовар разног      отпада на депонију',NULL,13,'Путни налогИзвештај са  депонијеДневни план депоновања','Одлагање отпада на депонији врши се површинским начином одлагања, разастирањем, компактирањем и прекривањем инертним материјалом. Возило које довози чврсте одпатке, интерном саобраћајницом одређеном у Дневном плану депоновања отпада долази до одређене радне зоне (радног чела) и врши истовар кабастог отпада и инертног материјала.','ВозачКомунални радници','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:21');
