INSERT INTO `FUK_AKTIVNOST` VALUES (3564,3046,3051,'Предаја путног      налога и пратеће        документације',NULL,19,'Путни налогПотврда о издатој количини гориваРадна листа','Возач предаје дежурном пословођи на сакупљању и транспорту отпада потписану радну листу са евидентираном реализацијим посла,потписани тахо листић и путни налог за возило.','ВозачПословођа на сакупљању и транспорту отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:22');
INSERT INTO `FUK_AKTIVNOST` VALUES (3565,3046,3051,'Обрада           рекламације',NULL,20,'','Корисник услуга сакупљања и транспорта отпада може да поднесе рекламацију  на извршену услугу код обједињене наплате, код Службе задужења и код РЈ „Сакупљање и транспорт отпада“. Подносилац рекламације,исту евидентира на рекламационом листу. Све поднете рекламације се прослеђују у РЈ „Сакупљање и транспорт отпада“. Координатор послова сакупљања и транспорта отпада прима рекламационе листове. Верзију рекламационог листа даје пословођи, који излази на терен истог дана, врши проверу стања и на полеђину рекламационог листа уписује чињенично стање.','','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:22');
INSERT INTO `FUK_AKTIVNOST` VALUES (3566,3046,3051,'Обрада            рекламације',NULL,21,'Рекламација Регистар рекламације','Корисник услуга сакупљања и транспорта отпада може да поднесе рекламацију  на извршену услугу код обједињене наплате, код Службе задужења и код РЈ „. На основу чињеничног стања, координатор послова сакупљања и транспорта отпада сачињава извештај, који оверава руководилац РЈ. Оверени извештај се доставља Служби евиденције корисника услуга на даље процесуирање. Координатор послова сакупљања и транспорта отпада у својој архиви чува верзије рекламационих листова и, на основу примљених рекламационих листова, води Регистар рекламација .','Руководилац РЈКординатор РЈПословођа на сакупљању и транспорту отпадаРуководилац службе евиденције корисника услуге','По потреби',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:22');
INSERT INTO `FUK_AKTIVNOST` VALUES (3567,3046,3051,'Извештавање',NULL,22,'Периодични извештаји','Координатор послова сакупљања и транспорта отпада припрема периодичне извештаје (дневне, месечне, годишње) које оверава руководилац Р.Ј.”Сакупљања и транспорта отпада” и исте доставља директору сектора управљања отпадом.','Директор сектораРуководилац РЈКоординатор РЈ','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:22');
