INSERT INTO `FUK_AKTIVNOST` VALUES (3590,3050,3055,'Визуелни преглед лица и возила на улазу/излазу',NULL,2,'Прописана списа и евиденције  по закону о ФТО и ИСО стандардима','Долазак службеника обезбеђења на штићену локацију и преузимање смене .Служб.обезбеђења контролише из и испред пријавнице – КПСт долазак и одлазак лица и возила на штићеној локацији. Врши најаву трећих лица По потреби врши преглед возила и пртљага, О свом раду и запажањима води прописане евиденције','Радник обезбеђења на смениЗапослени који прима посетиоца','У току сменесве време боравка посетиоца',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:24');
