INSERT INTO `FUK_AKTIVNOST` VALUES (3592,3050,3055,'Удаљење лица из штићених објеката и простора',NULL,4,'Прописана списа и евиденције  по закону о ФТО и ИСО стандардима','Службеник обезбеђења недозвољава улаз на штићену локацију: -трећем лицу које се не повинују прописаној процедури идентификације, -трећем лицу коме се не одобри посета-трећем лицу које се не придржава прописаних правила на штићеним локацијама или ремети ЈРМ и омета процес рада','Радник обезбеђења на смениЗапослени који прима посетиоца','У току сменесве време боравка посетиоца',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:24');
INSERT INTO `FUK_AKTIVNOST` VALUES (3593,3050,3055,'Привремено одузимање предмета и ствари',NULL,5,'Прописана списа и евиденције  по закону о ФТО и ИСО','Службеник обезбеђења у току контроле и прегледа запослених , може привремено одузети предмете и МТС за које не постоји прописана потребна документација или за која постоје основи сумње да ће се отуђити, као и да иста могу','Радник обезбеђења на смени','У току смене',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:24');
