INSERT INTO `FUK_AKTIVNOST` VALUES (3609,3052,3057,'Класификација           неправилности',NULL,2,'','Одлуку која се  врста класификације у поступку управљања неправилностима користи у оквиру предузећа , доноси директор,и то:-неправилности по врсти утицаја(финансијске,оперативне,репутационе);-неправилности по природи(системске,једнократне,грешке);-неправилности по области пословања(техничке,уговорне,административне,финансијске).','Директор','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:26');
INSERT INTO `FUK_AKTIVNOST` VALUES (3610,3052,3057,'Пријава            неправилности',NULL,3,'','Достављање обавештења/пријаве у вези са пријавом неправилности  врши се лицу задуженом за неправилности .','Узбуњивач','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:26');
INSERT INTO `FUK_AKTIVNOST` VALUES (3611,3052,3057,'Евидентирање о             сумњама на           неправилност',NULL,4,'','Информације о сумњама на неправилност се уносе у евиденцију о сумњама на неправилност која садржи:-радно место и име лица задуженог за пријем сумње на неправилност,-радно место и име лица које је уочило неправилнос или назив организационе целине од које је прослеђена сумња на неправилност,-датум и место,-назив пословног процеса,-редни број неправилности из наведеног процеса,-кратки опис сумње на неправилност,навођење приложених доказа,-одлука о даљњем поступању,-обавештење лицу које је уочило неправилност о одлуци о даљњем поступању по сумњи на неправилност,-доказ о прослеђивању лицу овлашћеном за пријем информације и вођење поступка у вези са узбуњивањем у складу са Законом о заштити узбуњивачау случају да сумња на неправилност има елементе кршења прописа,кршења људских права,вршења јавног овлашћења противно сврси због које је поверено,опасности по живот,јавно здравље,безбедност,животну средину,као и ради спречавања штете великих размера,-доказ о прослеђивању вишем нивоу руковођења сумње на неправилности које не могу да се разреше у делокругу рада и у оквиру овлашћења лица која су задужена за пријем сумњи на неправилност,-утврђена неправилност,-врста неправилности,-предузета активност за умањење ризика од уочене неправилности,-донета мера за отклањање  или умањење последица реализованих неправилности,-рок за отклањање узрока неправилности,-навођење доказа о реализацији амере за отклањање или умањење последица реализованих неправилности,-обавештење вишем руководству и организационој целини од које је добијена сумња на неправилност о предузетим активностима за умањење ризика и мерама за отклањање или умањење последица неправилности  и-информација од вишег руководства о предузетим активностима за умањење ризика и мерама за отклањање или умањење последица сумњи на неправилности које су им прослеђене.','Узбуњивач','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:26');
INSERT INTO `FUK_AKTIVNOST` VALUES (3612,3052,3057,'Провера             информација',NULL,5,'Записник, Приговор','У циљу провере информације у вези са пријавом неправилности  , предузимају се одговарајуће радње, о чему се обавештава послодавац, као и узбуњивач, уколико је то могуће на основу расположивих података. Ако се узимају изјаве од лица, у циљу провере информације у вези са пријавом неправилности  , о томе се саставља записник. На садржину записника, може се ставити приговор.','Лице задужено за неправилности','Најкасније у року од 15 дана',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:26');
INSERT INTO `FUK_AKTIVNOST` VALUES (3613,3052,3057,'Извештавање             послодавца и              узбуњивача',NULL,6,'Извештај о предузетим радњама у поступку о информацији у вези са пријавом неправилности','По окончању поступка саставља се извештај о предузетим радњама у поступку о информацији у вези са пријавом неправилности  , предлажу мере ради отклањања уочених неправилности и последица штетне радње насталих у вези са унутрашњим узбуњивањем.Извештај из става 1. овог члана, доставља се послодавцу и узбуњивачу, о коме се узбуњивач може изјаснити у року од 8 дана.','Лице задужено за неправилности','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:26');
INSERT INTO `FUK_AKTIVNOST` VALUES (3614,3052,3057,'ОДЛУКА',NULL,7,'Одлука, Извештај о предузетим радњама у поступку о информацији у вези са пријавом неправилности','Узбуњивач се може о извештају о предузетим радњама у поступку о информацији у вези са пријавом неправилности изјаснити у року од 2 дана од дана достављања извештаја','Узбуњивач','У року од 2 дана од дана достављања извештаја',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:26');
INSERT INTO `FUK_AKTIVNOST` VALUES (3615,3052,3057,'Предузимање                       мера',NULL,8,'Извештај о предузетим радњама у поступку о информацији у вези са пријавом неправилности','Ради отклањања уочених неправилности и последица штетне радње настале у вези са унутрашњим узбуњивањем могу да се предузму одговарајуће мере на основу извештаја.','Директор','У року од 15 дана',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:26');
