INSERT INTO `FUK_AKTIVNOST` VALUES (3632,3054,3059,'Планирање',NULL,4,'1 дан','Планирање','На основу израђених планова на недељном и дневном нивоу,руководилац РЈ,Координатор РЈ и пословођа израђују радне листе,радне и путне налоге и тахографски листић,након чега координатор даје усмени налог пословођама и радницима на којим реонима се ради.Пословођа издаје возачу посаде возила радну листу за фекалије.','Руководилац РЈКоординатор РЈПословођа на сакупљању и транспорту отпада',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:28');
INSERT INTO `FUK_AKTIVNOST` VALUES (3633,3054,3059,'Формирање                 екипа',NULL,5,'Свакодневно','Формирање                 екипа','Пословођа на сакупљању и транспорту отпада,врши формирање екипа за рад на реонима и то:У случају сакупљања фекалних вода, рејон-екипа се састоји из 2 члана (возач  који управља на командном тастеру на пражњењу септичких јама и 1 радник који учествује у пражњењу).','Пословођа на сакупљању и транспорту отпада',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:29');
INSERT INTO `FUK_AKTIVNOST` VALUES (3634,3054,3059,'Контрола',NULL,6,'Свакодневно','Контрола','Kонтрола реализације активности','Руководилац РЈКоординатор РЈ',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:29');
INSERT INTO `FUK_AKTIVNOST` VALUES (3635,3054,3059,'Пријем возила           од стране            возача у          контролној            служби',NULL,7,'По плану','Пријем возила           од стране            возача у          контролној            служби','Долазак радника у седиште предузећа и преузимање  возила од контролне службе, потребног за сакупљање неопасног отпада и транспортна депонију  на основу радне листе и усменог налога извршавају описане активности.Пословођа на терену контролише извршење радова по реонима.','.Пословођа РЈВозач',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:29');
INSERT INTO `FUK_AKTIVNOST` VALUES (3636,3054,3059,'Визуелна провера   техничке исправности              возила',NULL,8,'По плану','Визуелна провера   техничке исправности              возила','Визуелна провера техничке исправности возила од стране возача','Возач',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:29');
INSERT INTO `FUK_AKTIVNOST` VALUES (3637,3054,3059,'Преузимање радника     на прозивном месту',NULL,9,'По плану','Преузимање радника     на прозивном месту','Долазак на прозивно место возача са возилом','ВозачКомунални радници',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:29');
INSERT INTO `FUK_AKTIVNOST` VALUES (3638,3054,3059,'Пражњење и           транспорт       фекалних вода      из септичке јаме',NULL,10,'По плану','Пражњење и           транспорт       фекалних вода      из септичке јаме','Извршење активности на реону  на основу радне листе и усменог налога оперативци  извршавају описанеактивности. Инсталисање и укључивање пумпе за извлачење фекалних вода.Пословођа на терену контролише извршење радова по реонима.','Пословођа на сакупљању и транспорту комуналног отпадаВозачКомунални радник',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:29');
INSERT INTO `FUK_AKTIVNOST` VALUES (3639,3054,3059,'Евентаулна       комуникација са         пословођом',NULL,11,'По плану','Евентаулна       комуникација са         пословођом','Комуникација возача са пословођом на терену путем мобилних телефона у случају застоја у раду (квара на возилу и сл.)','Возач',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:29');
INSERT INTO `FUK_AKTIVNOST` VALUES (3640,3054,3059,'Испуштање у           градски                  каналисани        систем отпадних/              фекалних вода',NULL,12,'По плану','Испуштање у           градски                  каналисани        систем отпадних/              фекалних вода','Инсталисање и пуштање пумпе у градски каналисани систем  отпадних/фекалних вода','ВозачКомунални радник',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:29');
INSERT INTO `FUK_AKTIVNOST` VALUES (3641,3054,3059,'Одлазак на        прозивно место и            подношење       извештаја о раду',NULL,13,'По план','Одлазак на        прозивно место и            подношење       извештаја о раду','По извршењу активности на терену оперативци попуњавају радне и путне налоге.','Возач',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:29');
INSERT INTO `FUK_AKTIVNOST` VALUES (3642,3054,3059,'Прање и       танкирање возила',NULL,14,'По плану','Прање и       танкирање возила','Након реализације посла,при повратку посаде возила у ЈКП“Медиан“\"Ниш,возач врши прање и танкирање возила.','ВозачМагационер горива и мазиваПерач',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:30');
INSERT INTO `FUK_AKTIVNOST` VALUES (3643,3054,3059,'Пријава контролној      служби и провера              техничке          исправности               возила',NULL,15,'По плану','Пријава контролној      служби и провера              техничке          исправности               возила','Возач врши примопредају возила контролору техничке исправности возила Службе логистике и контроле,и истом врши пријаву о исправности возила у Радни налог-листа превентивног одржавања','ВозачКонтролор техничке исправности',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:30');
INSERT INTO `FUK_AKTIVNOST` VALUES (3644,3054,3059,'Паркирање возила',NULL,16,'По плану','Паркирање возила','Возач је у обавези да на прописан начин паркира возило у кругу предузећа','Возач',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:30');
INSERT INTO `FUK_AKTIVNOST` VALUES (3645,3054,3059,'Предаја путног           налога и            пратеће      документације',NULL,17,'По плану','Предаја путног           налога и            пратеће      документације','Возач предаје дежурном пословођи на сакупљању и транспорту отпада потписану радну листу са евидентираном реализацијим посла,потписани тахо листић и путни налог за возило.','ВозачПословођа на сакупљању и транспорту отпада',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:30');
