INSERT INTO `FUK_AKTIVNOST` VALUES (3666,3057,3062,'Информације о задатим објектима које треба унети у ГИС',NULL,2,'План рада службе за развој ГИС/ГПС','Координатор РЈ послова сакупљања и транспорта отпада  одговоран је за доставу списка локација контејнера.Оператер ГИС-а је одговоран за рад на терену и прикупљању информација о задатим објектима које треба унети у географско –информационим систем','Координатор ;Оператер ГИС-а,','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:32');
