INSERT INTO `FUK_AKTIVNOST` VALUES (3696,3061,3066,'Превентивнипреглед машине за рад',NULL,2,'Путни налог,Радни налогЕвиденција доливања уља и горива, Налог за гориво','Аутомеханичар врши  јутарњи преглед радних машина са руковаоцем радних машине по радном налогу за дневно одржавање машине, затим се сипа гориво у радну машину. После сипања горива се врши прање цистерном шмрковима па се опет врши преглед погонског строја и ако је све у реду машина је спремна за рад.','Контролор, Аутомеханичар,Руковалац радним машинама,Радник на депонији.','свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (3697,3061,3066,'Отварање радногналога у случајунедостатка инеисправности',NULL,3,'Путни налог,Радни налог','У случају евентуалних кварова и недостатака на радним машинама исто се пријављује техничком сектору за одржавање возила. Пријављивање се врши усмено/телефоном или писмено корективном мером.','Контролор, АутомеханичарРуковалац радним машинама','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (3698,3061,3066,'Излазак машинена терен',NULL,4,'Путни налог','Механичар отвара нови путни налог за текући радни дан,потписује путни налог,радни налог за ULT листу  дневне неге  или за техничку исправност и пушта се радна машина у рад','Руковалац радним машинама','свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (3699,3061,3066,'Истовар возила',NULL,5,'План дневног ангажовања радне снаге и машина,Дневни план одлагања отпада,Радни налог','Возило које довози чврсте одпатке, интерном саобраћајницом одређеном у Дневном плану депоновања отпада долази до одређене радне зоне (радног чела) и врши истовар комуналног отпада и инертног материјала. Ширина и дужина радне зоне (радног чела у складу са Планом дневног ангажовања радне снаге и машина  и  Дневним планом одлагања отпада), према налогу руководиоца РЈ „Депонија“, пословођа на депоновању отпада и руковалац радном машином, реализују технологију одлагања отпада на планираној касети сегмента поља депоније.','Возач возила,Руковалац радним машинама Пословођа','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (3700,3061,3066,'Разастирање икомпактирањеотпада',NULL,6,'Дневни  план одлагања отпадаПлан дневног ангажовања радне снаге и машина','Отпад се по довожењу систематски разастире булдожером тј. изравњава у слојевима дебљине 0.6m и сабија компактором до одређене густине  ccm0.8m3. На сваки формирани слој радна машина распростире следећи слој отпада преко радне површине и поново га сабија до висине ccm 2m. Ова операција се понавља у току целог дана по довожењу сваке количине отпада.','Возач возила,Руковалац радним машинама,Пословођа','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (3701,3061,3066,'Прекривка саодговарајућиминертнимматеријалом',NULL,7,'Дневни  план одлагања отпада,План дневног ангажовања радне снаге и машина,Дневни извештај','Одлагање отпада на депонији врши се површинским начином одлагања, разастирањем, компактирањем и прекривањем инертним материјалом Целокупна количина отпада која се распростре и сабије преко једне радне површине формиране у току једног радног дана по завршетку рада прекрива се инертним материјалом, који се насипа преко отпада на крају радног дана. Као прекривни материјали се користи земља и ситан грађевински материјал.','Руковаоци машина,Пословођа','Дневно и периодично',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:35');
