INSERT INTO `FUK_AKTIVNOST` VALUES (3722,3062,3069,'План прегледа громобранских инсталација у објектима.',NULL,2,'','Служба БЗР саставља План прегледа громобранских инсталација, које се обављају у зависности од нивоа заштите, на 2, 4 или на 6 година.','Сарадник за ЗОП','На крају године',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:37');
