INSERT INTO `FUK_AKTIVNOST` VALUES (3724,3062,3069,'Успостављање контакта са изабраним лицем, са којим је потписан Уговор.',NULL,4,'','Изабрано лице врши преглед громобранске инсталације, на свим локацијама у предузећу.','Сарадник за ЗОП','Према Уговору',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:37');
INSERT INTO `FUK_AKTIVNOST` VALUES (3725,3062,3069,'Достава извештаја о прегледу громобранских инсталација.',NULL,5,'Извештај о прегледу громобранске инсталације.','Изабрано лице након извршеног прегледа доставља  извештаје служби БЗР.','Сарадник за ЗОП','Према Уговору',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:37');
INSERT INTO `FUK_AKTIVNOST` VALUES (3726,3062,3069,'Контрола',NULL,6,'','Након добијања извештаја, врши се контрола да ли је на свим објектима извршен преглед громобранске инсталације.','Руководилац службе БЗР','Након доставе Извештаја',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:37');
