INSERT INTO `FUK_AKTIVNOST` VALUES (3730,3062,3070,'План обављања сервиса ПП апарата и испитивање хидрантске инсталације.',NULL,2,'Претходниналази','Служба БЗР саставља План обављања сервисирања, на основу чега се врши избор правног лица или предузетника, за вршење те услуге.','Сарадник за ЗОП','На крају године',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:38');
INSERT INTO `FUK_AKTIVNOST` VALUES (3731,3062,3070,'Успостављање контакта са изабраним извођачем испитивања.које обавља сервис ПП апарата и испитивање хидрантске инсталације',NULL,3,'','Изабрани извођач, са којим је потписан Уговор обавља сервис ПП апарата и испитивање хидрантске инсталације, на локацијама у предузећу где су постављени.','Сарадник за ЗОП','Према Уговору',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:38');
