INSERT INTO `FUK_AKTIVNOST` VALUES (3737,3062,3071,'План обуке запослених из области ЗОП-а, прави служба БЗР.',NULL,2,'Евиденција из Службе општих и кадровских послова','Сарадник за ЗОП прави План обуке.Обуку може да обавља сарадник за ЗОП или се бира треће лице са обављање тих послова.Обуку је потребно обавити пре почетка рада запосленог и на  сваке 3 године.','Сарадник за ЗОП','На крају године',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:38');
INSERT INTO `FUK_AKTIVNOST` VALUES (3738,3062,3071,'Успостављање контакта са изабраним лицем и спровођење Плана обуке.',NULL,3,'','У случају да обуку обавља правно лице, оно у сарадњи са сарадником за ЗОП, врши теоријску и практичну обуку.','Сарадник за ЗОП','Према Уговору',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:38');
INSERT INTO `FUK_AKTIVNOST` VALUES (3739,3062,3071,'Тестирање запослених',NULL,4,'','Након завршене обуке проверава се знање запослених и то теоријски и практично.','Сарадник за ЗОП','По извршеној обуци',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:38');
INSERT INTO `FUK_AKTIVNOST` VALUES (3740,3062,3071,'Контрола',NULL,5,'','Контрола да ли су одговарајући запослени оспособљени за санирање почетног пожара.','Руководилац службе БЗР','По извршеној обуци',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:38');
