INSERT INTO `FUK_AKTIVNOST` VALUES (3825,3069,3084,'Обављање припремних радњи',NULL,2,'Књиговодствене евиденције','Обављају се припремне радње за састављање годишњих финансијских извештаја - усаглашавање финансијских пласмана,потраживања и обавеза,спровођење и евидентирање пописа; усаглашавање главне и помоћне књиге (купаца и добављача,основних средстава,извршених исплата,остварених прилива,пласмана,дуга,примљених донација); обрачун и књижење амортизације,провера спроведене књиговодствене евиденције,отклањање евентуалних грешака и спровођење књижења на готовинској основи (по потреби).','Координатор одељења АОП-а,Самостални стручни сарадник за рачуноводство,контиста у рачуноводству,','Током године',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:45');
INSERT INTO `FUK_AKTIVNOST` VALUES (3826,3069,3084,'Контрола и усклађивање',NULL,3,'Књиговодствене евиденције','Обавља се контрола припремних радњи и врше се потребна усклађивања.','Руководилац службе рачуноводства','Током године',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:45');
INSERT INTO `FUK_AKTIVNOST` VALUES (3827,3069,3084,'Утврђивање билансне равнотеже',NULL,4,'Биланс стања','Утврђује се билансна равнотежа.Израђује се закључни лист и проверава се билансна равнотежа шематски,између класа.','Самостални стручни сарадник за рачуноводство,контиста у рачуноводству','Годишње',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:45');
INSERT INTO `FUK_AKTIVNOST` VALUES (3828,3069,3084,'Утврђивање финансијског резултата и књижења',NULL,5,'Финансијски резултат','Утврђује се суфицит и  дефицит.Обављају се одговарајућа књижења утврђеног финансијског резултата у главној књизи.','Самостални стручни сарадник за рачуноводство,контиста у рачуноводству','Годишње',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:45');
INSERT INTO `FUK_AKTIVNOST` VALUES (3829,3069,3084,'Попуњавање образаца',NULL,6,'Обрасци извештаја','Попуњавају се прописани обрасци финансијског извештаја.','Руководилац службе рачуноводства','Пре предаје',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:45');
INSERT INTO `FUK_AKTIVNOST` VALUES (3830,3069,3084,'Контрола',NULL,7,'Обрасци извештаја','Обавља се рачунска и логичка контрола образаца.','Помоћник директора за правно-економске послове','Након попуне',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:46');
INSERT INTO `FUK_AKTIVNOST` VALUES (3831,3069,3084,'Предаја образаца',NULL,8,'Обрасци извештаја','Предају се попуњени обрасци извештаја надлежном телу.Индиректни буџетски корисници прослеђују обрасце директним корисницима,док директни корисници састављају одлуку о завршном рачуну.','Руководилац службе рачуноводства','Према законским роковима предаје',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:46');
INSERT INTO `FUK_AKTIVNOST` VALUES (3832,3069,3084,'Закључивање и архивирање',NULL,9,'Пословне књиге и рачуноводствене исправеУредба о категоријама регистратурског материјала','Закључивање и чување пословних књига и рачуноводствених исправа.Закључивање пословних књига се врши најкасније до рока достављања финансијских извештаја,а чување у складу са Уредбом о буџетском рачуноводству (Службени гласник РС бр.125/03 и 12/06 и Уредбом о категоријама регистраторског материјала са роковима чувања (Службени гласник РС бр.44/93).','Руководилац службе рачуноводста,оператер','До рока доставе финансијских извештаја',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:46');
