INSERT INTO `FUK_AKTIVNOST` VALUES (3869,3071,3086,'Пријем возила од       стране возача у    контролној служби',NULL,7,'Радни налогПутни налог','Долазак радника у седиште предузећа и преузимање  возила од контролне службе, потребног за сакупљање и транспорт отпада, на основу радне листе и усменог налога извршавају описане активности..Пословођа на терену контролише извршење радова по реонима.','.Пословођа РЈВозач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:51');
INSERT INTO `FUK_AKTIVNOST` VALUES (3870,3071,3086,'Визуелна провера   техничке исправности               возила',NULL,8,'Путни налог','Визуелна провера техничке исправности возила од стране возача','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:51');
INSERT INTO `FUK_AKTIVNOST` VALUES (3871,3071,3086,'Преузимање радника   на прозивном месту',NULL,9,'Радна листа за контејнереПутни налог','Долазак на прозивно место возача са возилом','ВозачКомунални радници','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:51');
INSERT INTO `FUK_AKTIVNOST` VALUES (3872,3071,3086,'Сакупљање и           транспорт     комуналног отпада',NULL,10,'Радна листа за контејнереПутни налог','Извршење активности на реону  на основу радне листе и усменог налога оперативци  извршавају описанеактивности.Пословођа на терену контролише извршење радова по реонима. У случају промене локације и промене броја контејнера, која се уписује у радној  листи за контејнере, измена се евидентира у евиденцији локација контејнера  и о томе се обавештава Служба гис/гпс достављањем нове евиденције контејнера','Пословођа на сакупљању и транспорту комуналног отпадаВозачКомунални радник','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:51');
INSERT INTO `FUK_AKTIVNOST` VALUES (3873,3071,3086,'Евентуална     комуникација са        пословођом',NULL,11,'Путни налогКомуникација путем мобилних телефона','Комуникација возача са пословођом на терену путем мобилних телефона у случају застоја у раду (квара на возилу и сл.)','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:51');
INSERT INTO `FUK_AKTIVNOST` VALUES (3874,3071,3086,'Улазак возила у           депонију      Улазак возила у          депонију',NULL,12,'Путни налогЕвиденција доласка возила','Врши се евиденција доласка возила РЈ сакупљања и транспорта отпада која довозе комунални отпад..','ВозачКонтролор депоновања отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:51');
INSERT INTO `FUK_AKTIVNOST` VALUES (3875,3071,3086,'Мерење возила са  комуналним отпадом',NULL,13,'Путни налог','Врши се мерење на колску вагу пуног возила-празна возила се не мере јер су тежине програмски унешене.У дневном извештају евидентира се број тура и количина допремљеног комуналног отпада у(м3) и у (кг) параметар.','ВозачКонтролор депоновања отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:51');
INSERT INTO `FUK_AKTIVNOST` VALUES (3876,3071,3086,'Истовар комуналног     отпада на депонију',NULL,14,'Путни налогИзвештај са  депонијеДневни план депоновања','Одлагање отпада на депонији врши се површинским начином одлагања, разастирањем, компактирањем и прекривањем инертним материјалом. Возило које довози чврсте одпатке, интерном саобраћајницом одређеном у Дневном плану депоновања отпада долази до одређене радне зоне (радног чела) и врши истовар комуналног отпада и инертног материјала.','ВозачКомунални радници','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:51');
INSERT INTO `FUK_AKTIVNOST` VALUES (3877,3071,3086,'Излазак возила са     депоније и овера        путног налога',NULL,15,'Путни налогМернина','Уписује се време доласка/одласка ,име возача и број радника','Уписује се време доласка/одласка ,име возача и број радника','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:51');
INSERT INTO `FUK_AKTIVNOST` VALUES (3878,3071,3086,'Одлазак на   прозивно место и         подношење    извештаја о раду',NULL,16,'Путни налогМернина','По извршењу активности на терену оперативци попуњавају радне и путне налоге.','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:51');
INSERT INTO `FUK_AKTIVNOST` VALUES (3879,3071,3086,'Прање и   танкирање возила',NULL,17,'Радни налогПутни налог','Након реализације посла,при повратку посаде возила у ЈКП“Медиан“\"Ниш,возач врши прање и танкирање возила.','ВозачМагационер горива и мазиваПерач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:51');
INSERT INTO `FUK_AKTIVNOST` VALUES (3880,3071,3086,'Пријава контролној     служби и провера           техничке   исправности возила',NULL,18,'Путни налогПотврда о издатој количини горива','Возач врши примопредају возила контролору техничке исправности возила Службе логистике и контроле,и истом врши пријаву о исправности возила у Радни налог-листа превентивног одржавања','ВозачКонтролор техничке исправности','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:51');
INSERT INTO `FUK_AKTIVNOST` VALUES (3881,3071,3086,'Паркирање возила',NULL,19,'','Возач је у обавези да на прописан начин паркира возило у кругу предузећа','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:52');
INSERT INTO `FUK_AKTIVNOST` VALUES (3882,3071,3086,'Предаја путног     налога и пратеће        документације',NULL,20,'Путни налогПотврда о издатој количини гориваРадна листа','Возач предаје дежурном пословођи на сакупљању и транспорту отпада потписану радну листу са евидентираном реализацијим посла,потписани тахо листић и путни налог за возило.','ВозачПословођа на сакупљању и транспорту отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:52');
INSERT INTO `FUK_AKTIVNOST` VALUES (3883,3071,3086,'Обрада рекламације',NULL,21,'рекламација Регистар рекламације','Корисник услуга сакупљања и транспорта отпада може да поднесе рекламацију  на извршену услугу код обједињене наплате, код Службе задужења и код РЈ „Сакупљање и транспорт отпада“. Подносилац рекламације,исту евидентира на рекламационом листу. Све поднете рекламације се прослеђују у РЈ „Сакупљање и транспорт отпада“. Координатор послова сакупљања и транспорта отпада прима рекламационе листове. Верзију рекламационог листа даје пословођи, који излази на терен истог дана, врши проверу стања и на полеђину рекламационог листа уписује чињенично стање. На основу чињеничног стања, координатор послова сакупљања и транспорта отпада сачињава извештај, који оверава руководилац РЈ. Оверени извештај се доставља Служби евиденције корисника услуга на даље процесуирање. Координатор послова сакупљања и транспорта отпада у својој архиви чува верзије рекламационих листова и, на основу примљених рекламационих листова, води Регистар рекламација .','Руководилац РЈКординатор РЈПословођа на сакупљању и транспорту отпадаРуководилац службе евиденције корисника услуге','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:52');
INSERT INTO `FUK_AKTIVNOST` VALUES (3884,3071,3086,'Извештавање',NULL,22,'Рекламациа Регистар рекламације','Координатор послова сакупљања и транспорта отпада припрема периодичне извештаје (дневне, месечне, годишње) које оверава руководилац Р.Ј.”Сакупљања и транспорта отпада” и исте доставља директору сектора управљања отпадом.','Директор сектораРуководилац РЈКоординатор РЈ','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:52');
