INSERT INTO `FUK_AKTIVNOST` VALUES (3908,3074,3089,'Пријем возила           од стране            возача у          контролној             служби',NULL,7,'Радни налогПутни налог','Долазак радника у седиште предузећа и преузимање  возила од контролне службе, потребног за сакупљање неопасног отпада и транспортна депонију  на основу радне листе и усменог налога извршавају описане активности..Пословођа на терену контролише извршење радова по реонима.','.Пословођа РЈВозач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:55');
INSERT INTO `FUK_AKTIVNOST` VALUES (3909,3074,3089,'Визуелна провера  техничке исправности               возила',NULL,8,'Путни налог','Визуелна провера техничке исправности возила од стране возача','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:55');
INSERT INTO `FUK_AKTIVNOST` VALUES (3910,3074,3089,'Преузимање радника    на прозивном месту',NULL,9,'Радна листа за контејнереПутни налог','Долазак на прозивно место возача са возилом','ВозачКомунални радници','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:55');
INSERT INTO `FUK_AKTIVNOST` VALUES (3911,3074,3089,'Сакупљање и          транспорт       грађевинслог       неопасног отпада         шута и разног        другог отпада',NULL,10,'Радна листа за контејнереПутни налог','Извршење активности на реону  на основу радне листе и усменог налога оперативци  извршавају описанеактивности.Пословођа на терену контролише извршење радова по реонима. У случају промене локације и промене броја контејнера, која се уписује у радној  листи за контејнере, измена се евидентира у евиденцији локација контејнера  и о томе се обавештава Служба гис/гпс достављањем нове евиденције контејнера','Пословођа на сакупљању и транспорту комуналног отпадаВозачКомунални радник','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:55');
