INSERT INTO `FUK_AKTIVNOST` VALUES (3913,3074,3089,'Улазак возила на                депонију',NULL,12,'Путни налогЕвиденција доласка возила','Врши се евиденција доласка возила РЈ сакупљања и транспорта отпада која довозе кабасти отпад..','ВозачКонтролор депоновања отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:55');
INSERT INTO `FUK_AKTIVNOST` VALUES (3914,3074,3089,'Мерење возила са          грађевинским            неопасним               отпадом',NULL,13,'Путни налог','Врши се мерење на колску вагу пуног возила-празна возила се не мере јер су тежине програмски унешене.У дневном извештају евидентира се број тура и количина допремљеног комуналног отпада у(м3) и у (кг) параметар.','ВозачКонтролор депоновања отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:55');
INSERT INTO `FUK_AKTIVNOST` VALUES (3915,3074,3089,'Истовар    грађевинског разног     отпада на депонију',NULL,14,'Путни налогИзвештај са  депонијеДневни план депоновања','Одлагање отпада на депонији врши се површинским начином одлагања, разастирањем, компактирањем и прекривањем инертним материјалом. Возило које довози чврсте одпатке, интерном саобраћајницом одређеном у Дневном плану депоновања отпада долази до одређене радне зоне (радног чела) и врши истовар кабастог отпада и инертног материјала.','ВозачКомунални радници','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:55');
INSERT INTO `FUK_AKTIVNOST` VALUES (3916,3074,3089,'Излазак возила са       депоније и овера          путног налога',NULL,15,'Путни налогМернина','Уписује се време доласка/одласка ,име возача и број радника','Уписује се време доласка/одласка ,име возача и број радника','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:55');
