INSERT INTO `FUK_AKTIVNOST` VALUES (3924,3075,3090,'Израда процедуре за управљање ризицима по безбедност и здравље на раду',NULL,2,'Процедура, записи','Стрчни сарадници у служби БЗНР на основу Акта о процени ризика у свим процесима сагледавају ризике по безбееност и здравље на раду.У сарадњи са стручним сарадницима из службе квалитета постављају циљеве и програм мера за реализацију циљева.','Власници процеса','6 месеци',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:56');
INSERT INTO `FUK_AKTIVNOST` VALUES (3925,3075,3090,'Израда процедуре за истраживање инцидената и повреда на раду',NULL,3,'Процедура, записи','У поступку израде предметне процедуре сагледава се комплетна законска регулатива из области безбедности и здравља на раду.Прате се инцидентне ситуације и повреде на раду и о томе се сачинјавају одговарајући, законом предвиђени, записи.','Власници процеса','6 месеци',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:56');
