INSERT INTO `FUK_AKTIVNOST` VALUES (3932,3076,3091,'Право на           коришћење        репрезентације',NULL,3,'','Право на коришћење репрезентације има директор ,помоћници директора ,извршни директори,директори сектора који су непосредно одговорни извршним директорима,Председник и чланови Надзорног одбора.Право на коришћење репрезентације могу имати и друга лица запослена у Предузећу на основу писменог одобрења (овлашћења)директора Предузећа.','Директор','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:57');
INSERT INTO `FUK_AKTIVNOST` VALUES (3933,3076,3091,'Прекорачење         износа трошкова          репрезентације',NULL,4,'','Корисник права на репрезентацију учињене трошкове репрезентације дужан  је да надокнади Предузећу уколико директор накнадно не одобри трошкове репрезентације у пуном износу у случају неовлашћеног и ненаменског коришћења средстава,прекорочења дозвољене висине трошкова и у случају када рачун о коришћеним услугама не садржи прописану документацију.','Корисник','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:57');
INSERT INTO `FUK_AKTIVNOST` VALUES (3934,3076,3091,'Контрола',NULL,5,'','Рачуне који се односе на репрезентацију,након овере корисника услуга репрезентације,контролише служба финансије-одељење платног промета која потврђују њихову тачност.Контролу утрошка средстава за репрезентацију врше Помоћник директора за правно-економске послове и Директор сектор економских послова на основу месечних извештаја о трошковима репрезентације које доставља служба рачуноводства-одељење рачуноводства приликом разматрања кварталних извештаја о степену усклађености планираних  и реализованих активности из програма пословања.','Помоћник директора за правно-економске послове  Директор секторa економских послова','',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:57');
INSERT INTO `FUK_AKTIVNOST` VALUES (3935,3076,3091,'Овера документа',NULL,6,'Рачун о коришћењу средстава','Лице које је користило средства репрезентације дужно је да сачини извештај о насталим трошковима репрезентације уз који прилаже рачун за настале трошкове на чијој полеђини ставља свој потпис. Рачун о коришћењу средстава садржи спецификацију трошкова по врсти и количини – фискални рачун.','Помоћник директора за правно-економске послове и Директор сектора економских послова','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:39:57');
