INSERT INTO `FUK_AKTIVNOST` VALUES (4032,3086,3102,'Анализа учинка,фактурисање иизвештавање',NULL,9,'Месечна ситуацијаЗакључницаотпремницаПрилог рачуну','На основу потписаних грађевинских дневника од стране надзорног органа, референт израђује недељни извештај,који омогућава праћење месечне динамике послова. На основу потписаних грађевинских дневника и оверених недељних извештаја референт обједињава месечну ситуаци по обиму извршених радова и шаље економском сектору ради испостављања коначне месечне ситуације. За услуге трећим лицима, на основу оверених грађевинских дневника, прави се прилог рачуну на основу ког економски сектор израђује рачун.Ова документа одобравају директор сектора „Зеленило“ и Директор.','Директор, Директор сектора,Референт','Дневномесечнонакон завршене услуге',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:06');
