INSERT INTO `FUK_AKTIVNOST` VALUES (4059,3088,3105,'Слање обавештења руководиоцима  сектора, о времену и месту добровољног давања крви.',NULL,4,'Допис','Седам дана пре одређеног рока, обавештавају се руководиоци сектора о времену извршења акције.','Сарадник за послове БЗР.','7 дана пре почетка',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:09');
