INSERT INTO `FUK_AKTIVNOST` VALUES (4061,3088,3105,'Достава потврда и књижица запослених, који су добровољно дали крв.',NULL,6,'Извештај и потврде','Након завршетка акције, Завод за трансфузију крви доставља предузећу потврде и књижице свим запосленима који су се одазвали акцији.','Сарадник за послове БЗР.','Након завршетка акције',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:09');
