INSERT INTO `FUK_AKTIVNOST` VALUES (4065,3088,3106,'Израда плана периодичног прегледа опреме за рад.',NULL,2,'Правилник о периодичном прегледу опреме за рад;Акт о процени ризика;Претходни стручни налази.','Руководилац Службе БЗР и  сарадник за послове БЗР израђују План за обављање периодичног прегледа опреме за рад.','Сарадник за послове БЗР','На крају године',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:09');
INSERT INTO `FUK_AKTIVNOST` VALUES (4066,3088,3106,'Испитивање тржишта.',NULL,3,'','Испитивање тржишта на основу чега се бира правно лице или предузетник за обављање тих послова.','Сарадник за послове БЗР','Према Плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:09');
INSERT INTO `FUK_AKTIVNOST` VALUES (4067,3088,3106,'Преглед опреме за рад од стране изабраног правног лица са лиценцом',NULL,4,'Важећи стручни налаз,техничка документација.','Изабрани извођачи услуге врше преглед опреме за рад, на свим локацијама у предузећу.','Сарадник за послове БЗР, стручно лице понуђача.','На три године, по потреби.',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:09');
INSERT INTO `FUK_AKTIVNOST` VALUES (4068,3088,3106,'Израда и достава стручних налаза опреме за рад, од стране извршиоца испитивања.',NULL,5,'Подаци узети из техничке документације опреме за рад за коју се врши периодични преглед и испитивање, стари стручни налаз.','Извршиоц испитивања након извршене контроле и прегледа, доставља стручне налазе Служби БЗР.','Сарадник за послове БЗР, стручна лица понуђача','15 дана након извршеног прегледа',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:09');
INSERT INTO `FUK_AKTIVNOST` VALUES (4069,3088,3106,'Контрола',NULL,6,'Стручни налазиза опрему за рад.','Руководилац и сарадник за послове БЗР контролишу достављене стручне налазе.','Руководилац, сарадник за послове БЗР','Одмах по пријему стручних налаза.',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:09');
INSERT INTO `FUK_AKTIVNOST` VALUES (4070,3088,3106,'Слање обавештења секторима, у којима је уочена неисправност опреме за рад.',NULL,7,'Стручни налаз','Слање обавештења или забране рада, секторима у којима је уочена неисправност опреме за рад.','Сарадник за послове БЗР','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:10');
