INSERT INTO `FUK_AKTIVNOST` VALUES (4074,3088,3107,'Израда Плана за испитивање услова РО.',NULL,2,'Претходни Уговор','Служба БЗР прави План спровођења које се обавља на три године.Испитивања микроклиме врше се у летњем и зимском периоду (2 пута годишње).План садржи и испитивање тржишта.','Сарадник за послове БЗР,руководилац Службе БЗР','Крајем године',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:10');
INSERT INTO `FUK_AKTIVNOST` VALUES (4075,3088,3107,'Успостављање контакта са потенцијалним извршиоцима испитивања.',NULL,3,'','Успостављање контакта са представником правног лица са лиценцом за обављање послова испитивања.','Сарадник за послове БЗР, руководилац Службе БЗР','Према Плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:10');
INSERT INTO `FUK_AKTIVNOST` VALUES (4076,3088,3107,'Испитивање услова радне околине, од стране правног лица са лиценцом којем је поверено испитивања услова РО.',NULL,4,'','Обављају се  испитивања услова радне околине, у свим радним и помоћним просторијама у предузећу - провера микроклиме (температура, влажност и струјање ваздуха), осветљења и физичких, хемијских и биолошких штетности.','Сарадник за послове БЗР','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:10');
INSERT INTO `FUK_AKTIVNOST` VALUES (4077,3088,3107,'Контрола',NULL,5,'','Провера да ли је спроведено испитивање у свим радним и помоћним просторијама на свим локацијама у предузећу.','Руководилац Службе БЗР','.',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:10');
