INSERT INTO `FUK_AKTIVNOST` VALUES (4083,3088,3108,'Испитивање тржишта.',NULL,3,'Телефон, и-мејл','Испитивање тржишта према Плану за спровођење.','РуководилацСлужбе БЗР, сарадник за послове БЗР.','Према Плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:10');
INSERT INTO `FUK_AKTIVNOST` VALUES (4084,3088,3108,'Успостављање контакта са изабраним извршиоцем услуге за обављање послова дезинсекције и дератизације.',NULL,4,'Телефон, и-мејл','Извршиоц услуге обавља дезинсекцију и дератизацију у свим просторијама у предузећу, на свака 3 месеца (четири пута годишње) и по потреби.','Сарадник за послове БЗР','Уговор,            по Плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:10');
