INSERT INTO `FUK_AKTIVNOST` VALUES (4090,3088,3109,'Израда Плана за организовање санитарног прегледа',NULL,2,'Евиденција запослених и Извештаји о претходним санитарним прегледима.','Израда Плана организовања санитарних прегледа за запослене, који су у обавези да их обављају, и склапање Уговора са Установом која ће обавити прегледе.','Сарадник за послове БЗР, сарадник за јавну набавку.','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (4091,3088,3109,'Сакупљање података о запосленима које подлежу обављању санитарног прегледа',NULL,3,'Евиденција запослених','Запослени који раде на радном месту издавалац топлог оброка и опслужилац кафе бифеа,пре ступања на рад, и на сваких 6 месеци у току рада, морају урадити санитарни преглед.','Администратор за послове БЗР','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (4092,3088,3109,'Израда и достава  упута запосленима.',NULL,4,'Упути за санитарне прегледе','Администратор израђује упуте за санитарни преглед, који се оверавају и шаљу у Институт за јавно здравље.','Администратор за послове БЗР','Према Плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (4093,3088,3109,'Контрола',NULL,5,'','Контрола да ли су сви запослени, обавештени и добили упуте о прегледу.','Руководилац службе БЗР, непосредни руководилац запослених које подлежу санитарним прегледима.','',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (4094,3088,3109,'Евиденција запослених, који су обавили санитарне прегледе.',NULL,6,'Извештај о обављеним прегледима,санитарне књижице.','Након обављеног прегледа, Институт за ЈЗ прослеђује поштом Извештаје о завршеним санитарним  прегледима, а Служба БЗР води евиденцију о томе.','Администратор за послове БЗР','По обављеним прегледима.',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:11');
