INSERT INTO `FUK_AKTIVNOST` VALUES (4097,3088,3110,'Организовање оспособљавања запослених за безбедан и здрав рад',NULL,2,'Уговор о раду, анекс уговора о раду.','Служба БЗР организује обуку  новозапослених и запослених који су распоређени на друга радна места.','Сарадник за послове БЗР,руководилац Службе БЗР','По потреби, по пријему.',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (4098,3088,3110,'Спровођење обуке запослених, оспособљавање за БЗР и оспособљавање на основу Програма за руковање опремом за рад, за радна места која захтевају посебне вештине и знања, из области БЗР.',NULL,3,'Уговор о раду, Анекс уговора о раду.','Сарадник за послове БЗР обавља оспособљавање из области БЗР, након пријема запослених и распоређивања током рада (промена радног места).Комисија за стручно оспособљавање обавља обуку за раљдна места која захтевају посебне вештине и знања, из области БЗР.','Сарадник за послове БЗР, Комисија за стручно оспособљавање запослених','По потреби',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (4099,3088,3110,'Контрола',NULL,4,'Документација о извршеној обуци.','Служба БЗР обавља контролу евиденције запослених који су оспособљени.','Руководилац БЗР','Након обуке',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (4100,3088,3110,'Тестирање запослених(теоријски и практично).',NULL,5,'Тест о извршеној обуци.','Запослени се упознају са теоријским знањима из БЗРа обављају и практични део обуке (начин употребе и коришћења средстава и опреме личне заштите).','Сарадник за послове БЗР','Након обуке',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (4101,3088,3110,'Евиденција образаца 6, према Правилнику о евиденцијама у области БЗР.',NULL,6,'Образац бр.6','Служба БЗР води евиденцију о обукама за БЗР,за све запослене у предузећу.','Администратор за послове БЗР','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:12');
